//Function to generate words
function generateWords(inputArray, noOfWords) {
    let outputText = "";
    for (let i = 0; i < noOfWords; i++) {
        let randomWords = inputArray[Math.floor(Math.random() * inputArray.length)];
        outputText += `${randomWords} `;
    }
    return outputText;
}
//Function to generate sentences
function generateSentences(inputArray, noOfSentences, language) {
    //One sentence to have 4-8 words. Punct mark at end.
    let outPutText = "";
    if (language === "Hindi" || language === "Bangla" || language === "Odia" || language === "Punjabi") {
        for (let i = 0; i < noOfSentences; i++) {
            outPutText += generateWords(inputArray, randomInt(4, 9)); //+ punctSetTwo[randomInt(0,4)];
            if (i === noOfSentences - 1) {
                outPutText += generateWords(inputArray, randomInt(4, 9)); //+ punctSetTwo[0];
            }
        }
    }
    else if (language === "Sanskrit") {
        for (let j = 0; j < noOfSentences; j++) {
            outPutText += generateWords(inputArray, randomInt(4, 9)); //+ punctSetThree[1];
            if (j === noOfSentences - 1) {
                outPutText += generateWords(inputArray, randomInt(4, 9)); //+ punctSetThree[0];
            }
        }
    }
    else {
        for (let j = 0; j < noOfSentences; j++) {
            outPutText += generateWords(inputArray, randomInt(4, 9)); //+ punctSetOne[randomInt(0,2)];
            if (j === noOfSentences - 1) {
                outPutText += generateWords(inputArray, randomInt(4, 9)); // + punctSetOne[0];
            }
        }
    }
    return outPutText;
}
//Function to generate paragraphs
function generateParagraphs(inputArray, noOfParagraphs, language) {
    //One sentence to have 3-6 sentences. Punct mark at end. A line break between each para
    let outPutText = "";
    for (let k = 0; k < noOfParagraphs; k++) {
        outPutText += generateSentences(inputArray, randomInt(3, 7), language) + "\n" + "\n";
    }
    return outPutText;
}
//Function to generate random integer for sent and para
function randomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min) + min);
}
